import React, { useState, useEffect } from "react";
import { Col, Form, Button, Row, Container, Card } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import Header from "./component/Header";
//import Validation from "./component/Validation";
export default function Register(prop) {
  const [val, setVal] = useState({
    name: "",
    email: "",
    password: "",
    contact: "",
    city: "",
    state: "",
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  function handleChange(e) {
    const name = e.target.name;
    const value = e.target.value;
    setVal({ ...val, [name]: value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    setFormErrors(validate(val));
    setIsSubmit(true);
    fetch("http://localhost:8000/Register", {
      method: "Post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(val),
    }).then((reqs) => {
      reqs.json().then((result) => {
        console.log(result);
      });
    });

    console.log(val);
    localStorage.setItem("UserEmail", val.email);
    localStorage.setItem("UserPassword", val.password);
    prop.history.push("/login");
    setVal({});
  }

  useEffect(() => {}, []);
  const validate = (values) => {
    const errors = {};
    if (!values.name) {
      errors.name = "name is require";
    }
    if (!values.email) {
      errors.email = "email is require";
    }
    if (!values.password) {
      errors.password = "password is require";
    }
    if (!values.contact) {
      errors.contact = "contact is require";
    }
    if (!values.city) {
      errors.city = "city is require";
    }
    if (!values.state) {
      errors.state = "state is require";
    }
    return errors;
  };
  return (
    <div>
      <Header />
      <Container>
        <Card style={{ padding: 100, margin: 120 }}>
          <Form onSubmit={handleSubmit}>
            <h1 style={{ textAlign: "center", color: "blue" }}>Registration Page</h1>
            <Form.Group as={Col} controlId="formGridName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                name="name"
                onChange={handleChange}
              />
              {formErrors.name && <p>{formErrors.name}</p>}
            </Form.Group>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter email"
                  name="email"
                  onChange={handleChange}
                />
                {errors.email && <p>{errors.email}</p>}
              </Form.Group>

              <Form.Group as={Col} controlId="formGridPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  name="password"
                  onChange={handleChange}
                />
                {errors.password && <p>{errors.password}</p>}
              </Form.Group>
            </Row>
            <Form.Group className="mb-3" controlId="formGridContact">
              <Form.Label>Contact</Form.Label>
              <Form.Control placeholder="987654310" name="contact" onChange={handleChange} />
              {errors.contact && <p>{errors.contact}</p>}
            </Form.Group>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridCity">
                <Form.Label>City</Form.Label>
                <Form.Control name="city" onChange={handleChange} />
                {errors.city && <p>{errors.city}</p>}
              </Form.Group>
              <Form.Group as={Col} controlId="formGridCity">
                <Form.Label>State</Form.Label>
                <Form.Control name="state" onChange={handleChange} />
                {errors.state && <p>{errors.state}</p>}
              </Form.Group>
            </Row>
            <Form.Group className="mb-3" id="formGridCheckbox">
              <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Register
            </Button>
            <span style={{ margin: "0 20px" }}>Aleady have a account</span>
            <Link to="/login">Login</Link>
          </Form>
        </Card>
      </Container>
    </div>
  );
}
