import React, { useState } from "react";
import { Col, Form, Button, Row, Container, Card } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import Header from "./component/Header";
import FormValidation from "./component/FormValidation";
export default function Register(prop) {
  const [val, setVal] = useState({
    name: "",
    email: "",
    password: "",
    contact: "",
    city: "",
    state: "",
  });
  const [formError, setFormError] = useState({});
  function handleChange(e) {
    const name = e.target.name;
    const value = e.target.value;
    setVal({ ...val, [name]: value });
    setFormError({
      ...formError,
      [e.target.name]: FormValidation.Registration(e.target.name, e.target.value),
    });
    console.log(formError);
  }

  function handleSubmit(e) {
    e.preventDefault();
    fetch("http://localhost:8000/Register", {
      method: "Post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(val),
    }).then((reqs) => {
      reqs.json().then((result) => {
        console.log(result);
      });
    });

    console.log(val);
    localStorage.setItem("UserEmail", val.email);
    localStorage.setItem("UserPassword", val.password);
    prop.history.push("/login");
    setVal({});
  }

  return (
    <div>
      <Header />
      <Container>
        <Card style={{ padding: 100, margin: 120 }}>
          <Form onSubmit={handleSubmit}>
            <h1 style={{ textAlign: "center", color: "blue" }}>Registration Page</h1>
            <Form.Group as={Col} controlId="formGridName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                name="name"
                onChange={handleChange}
              />
              {formError.name && <div style={{ color: "red", fontSize: 14 }}>{formError.name}</div>}
            </Form.Group>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter email"
                  name="email"
                  onChange={handleChange}
                />
                {formError.email && <div>{formError.email}</div>}
              </Form.Group>

              <Form.Group as={Col} controlId="formGridPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  name="password"
                  onChange={handleChange}
                />
                {formError.password && <div>{formError.password}</div>}
              </Form.Group>
            </Row>
            <Form.Group className="mb-3" controlId="formGridContact">
              <Form.Label>Contact</Form.Label>
              <Form.Control placeholder="987654310" name="contact" onChange={handleChange} />
            </Form.Group>
            {formError.contact && <div>{formError.contact}</div>}
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridCity">
                <Form.Label>City</Form.Label>
                <Form.Control name="city" onChange={handleChange} />
              </Form.Group>
              {formError.city && <div>{formError.city}</div>}
              <Form.Group as={Col} controlId="formGridCity">
                <Form.Label>State</Form.Label>
                <Form.Control name="state" onChange={handleChange} />
              </Form.Group>
              {formError.state && <div>{formError.state}</div>}
            </Row>
            <Form.Group className="mb-3" id="formGridCheckbox">
              <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Register
            </Button>
            <span style={{ margin: "0 20px" }}>Aleady have a account</span>
            <Link to="/login">Login</Link>
          </Form>
        </Card>
      </Container>
    </div>
  );
}
